package com.relesi.jwt.dto;import com.fasterxml.jackson.annotation.JsonIgnore;import com.relesi.jwt.domain.User;import com.relesi.jwt.enums.Profile;import java.io.Serializable;public class AuthorDTO implements Serializable {    private static final long serialVersionUID = 1L;    private String id;    private  String name;    private String email;    private String password;    private Profile profile;    public AuthorDTO(){    }    public AuthorDTO(User obj){        id = obj.getId();        name = obj.getName();        email = obj.getEmail();        password = obj.getPassword();        profile = obj.getProfile();    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public Profile getProfile() {        return profile;    }    public void setProfile(Profile profile) {        this.profile = profile;    }}