package com.relesi.jwt.dto;import com.relesi.jwt.domain.User;import org.springframework.data.annotation.Id;import java.io.Serializable;public class UserDTO implements Serializable {    private static final long serialVersionUID = 1L;    @Id    private String id;    private String name;    private String senha;    private String email;    public UserDTO() {    }    public UserDTO(User obj){        id = obj.getId();        name = obj.getName();        senha = obj.getSenha();        email = obj.getEmail();    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getSenha() {        return senha;    }    public void setSenha(String senha) {        this.senha = senha;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }}