package com.relesi.jwt.dto;import com.relesi.jwt.domain.User;import com.relesi.jwt.enums.Profile;import org.springframework.data.annotation.Id;import java.io.Serializable;public class UserDTO implements Serializable {    private static final long serialVersionUID = 1L;    @Id    private String id;    private String name;    private String email;    private String password;    private Profile profile;    public UserDTO() {    }    public UserDTO(String id, String name, String email) {        this.id = id;        this.name = name;        this.email = email;    }    public UserDTO(User obj) {        id = obj.getId();        name = obj.getName();        email = obj.getEmail();        password = obj.getPassword();        profile = obj.getProfile();    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }}