package com.relesi.jwt.repository;import com.relesi.jwt.domain.Admin;import org.springframework.data.mongodb.repository.MongoRepository;import org.springframework.data.mongodb.repository.Query;import org.springframework.stereotype.Repository;import java.util.Date;import java.util.List;@Repositorypublic interface AdminRepository extends MongoRepository<Admin, String> {    @Query("{ 'title': { $regex: ?0, $options: 'i' } }")    List<Admin> searchTitle(String text);    List<Admin> findByTitleContainingIgnoreCase(String text);    @Query("{ $and: [ { date: {$gte: ?1} }, { date: { $lte: ?2} } , { $or: [ { 'title': { $regex: ?0, $options: 'i' } }, { 'body': { $regex: ?0, $options: 'i' } }, { 'comments.text': { $regex: ?0, $options: 'i' } } ] } ] }")    List<Admin> fullSearch(String text, Date minDate, Date maxDate);}