package com.relesi.jwt.services;import com.relesi.jwt.domain.Admin;import com.relesi.jwt.repository.AdminRepository;import com.relesi.jwt.exceptions.ObjectNotFoundException;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Date;import java.util.List;import java.util.Optional;@Servicepublic class AdminService {    @Autowired    private AdminRepository repo;    public Admin findById(String id) {        Optional<Admin> obj = repo.findById(id);        return obj.orElseThrow(() -> new ObjectNotFoundException("Object not found"));    }    public List<Admin> findByTitle(String text) {        return repo.searchTitle(text);    }    public List<Admin> fullSearch(String text, Date minDate, Date maxDate) {        maxDate = new Date(maxDate.getTime() + 24 * 60 * 60 * 1000);        return repo.fullSearch(text, minDate, maxDate);    }}